import React from 'react';
import Dropzone from 'react-dropzone';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import {image64toCanvasRef, extractImageFileExtensionFromBase64, downloadBase64File, base64StringtoFile, makeClientCrop} from './ResuableUtils';


const imgMaxSize = 100000000//bytes;
const FileTypes = ['image/x-png', 'image/png', 'image/jpg', 'image/jpeg', 'image/gif']
class App extends React.Component {
  //const {acceptedFiles, getRootProps, getInputProps} = useDropzone();
  constructor(props) {
    super(props)
    this.imagePreviewCanvasRef = React.createRef();
    this.fileInputRef = React.createRef()
    this.state = {
        imgSrc: null,
        croppedImageURL: null, 
        crop: {
            unit: 'px',
            height: 755,
            width: 450

        } 
    }
  }


    alertFile = (files) => {
        if(files && files.length>0){
            const currentFile = files[0];
            const currentFileSize = currentFile.size;
            const currentFileType = currentFile.type;
            if(currentFileSize > imgMaxSize){
                alert("This file is too big");
                return false;
            }
            if(!FileTypes.includes(currentFileType)){
                alert("This file type is not supported");
                return false;
            }
            return true;
        }

    };

    onDropFn = (files, rejectedFiles) => {
        //console.log(files);
        if(rejectedFiles && rejectedFiles.length > 0){
           this.alertFile(rejectedFiles);
        }
        if(files && files.length>0 ){
            const isVerified = this.alertFile(files)
            if(isVerified) {
                const currentFile = files[0]
                const reader = new FileReader()
                reader.addEventListener("load", () => {
                    this.setState({
                        imgSrc: reader.result
                    })

                }, false)

                reader.readAsDataURL(currentFile)
            }


        }
    };


    handleImageLoaded = (image) => {
        //console.log(image)
        //this.imageRef = image;
    };
    handleOnCropChange = (crop) => {
        console.log(crop)
        this.setState({crop:crop})
        //console.log(this.state)
    };
    handleOnCropComplete = (crop, pixelCrop) => {
        console.log(pixelCrop);
        console.log(crop);
        const canvasRef = this.imagePreviewCanvasRef.current;
        const {imgSrc} = this.state;
        image64toCanvasRef(canvasRef, imgSrc, crop);
    };

    handleDownloadClick = (event) => {
        event.preventDefault()
        const {imgSrc}  = this.state
        if (imgSrc) {
            const canvasRef = this.imagePreviewCanvasRef.current

            const {imgSrcExt} =  this.state
            const imageData64 = canvasRef.toDataURL('image/' + imgSrcExt)


            const myFilename = "previewFile." + imgSrcExt

            // file to be uploaded
            const myNewCroppedFile = base64StringtoFile(imageData64, myFilename)
            console.log(myNewCroppedFile)
            // download file
            downloadBase64File(imageData64, myFilename)
            this.handleClearToDefault()
        }
    }

    handleClearToDefault = (event) =>{
        if (event) event.preventDefault()
        const canvas = this.imagePreviewCanvasRef.current
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height)

        this.setState({
            imgSrc: null,
            imgSrcExt: null,
            crop: {
                aspect: 1/1
            }

        })
        this.fileInputRef.current.value = null
    }

render(){
    const {imgSrc, croppedImageURL} = this.state
      return (
        <div>
        <Dropzone  onDrop={this.onDropFn} accept={FileTypes} multiple={false} maxSize={imgMaxSize}>
            {({getRootProps, getInputProps}) => (
                <section>
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    <button style = {{marginTop: "140px", marginLeft: "740px"}} ><p> Click here to select a file</p></button>
                  </div>
                </section>
            )}
        </Dropzone>
        {imgSrc !== null ? 
            <div> 
                <ReactCrop src = {imgSrc} 
                crop = {this.state.crop} 
                onImageLoaded = {this.handleImageLoaded}
                onComplete = {this.handleOnCropComplete}
                onChange = {this.handleOnCropChange}/>

            <br/>
            <p> Preview Canvas Crop </p>
            <canvas ref={this.imagePreviewCanvasRef}></canvas>
             <button onClick={this.handleDownloadClick}>Download</button>
              <button onClick={this.handleClearToDefault}>Clear</button>
            </div>
            : ""}

            {/* croppedImageURL &&  <img alt="Cropped Image" style={{maxWidth: '100%'}} src={croppedImageURL} /> */}

        </div>

      );
    }
}

export default App;
