import React from 'react';
import Dropzone from 'react-dropzone';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import {image64toCanvasRef, extractImageFileExtensionFromBase64, downloadBase64File, base64StringtoFile, makeClientCrop} from './ResuableUtils';
import { Upload, message, Icon, PageHeader, Tabs, Button, Statistic, Descriptions } from 'antd';
import { Menu, Dropdown, Row, Col } from 'antd';
import 'antd/dist/antd.css'
const imgMaxSize = 100000000//bytes;
const FileTypes = ['image/x-png', 'image/png', 'image/jpg', 'image/jpeg', 'image/gif']
class App extends React.Component {
  //const {acceptedFiles, getRootProps, getInputProps} = useDropzone();
  constructor(props) {
    super(props)
    this.imagePreviewCanvasRef = React.createRef();
    //this.fileInputRef = React.createRef()
    this.state = {
        imgSrc: null,
        croppedImageURL: null, 
        crop: {
            unit: 'px',
            height: 755,
            width: 450

        } 
    }
  }


    alertFile = (files) => {
        if(files && files.length>0){
            const currentFile = files[0];
            const currentFileSize = currentFile.size;
            const currentFileType = currentFile.type;
            if(currentFileSize > imgMaxSize){
                alert("This file is too big");
                return false;
            }
            if(!FileTypes.includes(currentFileType)){
                alert("This file type is not supported");
                return false;
            }
            return true;
        }

    };

    onDropFn = (files, rejectedFiles) => {
        //console.log(files);
        if(rejectedFiles && rejectedFiles.length > 0){
           this.alertFile(rejectedFiles);
        }
        if(files && files.length>0 ){
            const isVerified = this.alertFile(files)
            if(isVerified) {
                const currentFile = files[0]
                const reader = new FileReader()
                console.log(reader);
                reader.addEventListener("load", () => {
                    let image = new Image();
                    image.src = reader.result;

                    let IncludePic = 0
                    image.onload = () => {
                        if(image.width !== 1024 || image.height !== 1024){
                            message.info(`Image is ${image.height} x ${image.width}$. Please upload 1024 x 1024 image`);
                        }
                        else
                        {
                            this.setState({
                            imgSrc: reader.result,
                            imgSrcExt:  extractImageFileExtensionFromBase64(reader.result)
                            })

                        }
                    };

                }, false)

                reader.readAsDataURL(currentFile)
            }


        }
    };


    handleImageLoaded = (image) => {
        //console.log(image)
        //this.imageRef = image;
    };
    handleOnCropChange = (crop) => {
        console.log(crop)
        this.setState({crop:crop})
        //console.log(this.state)
    };
    handleOnCropComplete = (crop, pixelCrop) => {

        const canvasRef = this.imagePreviewCanvasRef.current;
        console.log(canvasRef);
        const {imgSrc} = this.state;
        image64toCanvasRef(canvasRef, imgSrc, crop);
    };

    handleClearToDefault = (event) =>{
        if (event) event.preventDefault()
        const canvas = this.imagePreviewCanvasRef.current
        const ctx = canvas.getContext('2d');
        console.log(canvas);
        ctx.clearRect(0, 0, canvas.width, canvas.height)

        this.setState({
            imgSrc: null,
            imgSrcExt: null,


        })
        //this.fileInputRef.current.value = null
    };

    onClick = ({ key }) => {
    message.info(`Click on item ${key}`);
    };

    menu = (
    <Menu onClick={this.onClick}>
    <Menu.Item key="1">755 x 450</Menu.Item>
    <Menu.Item key="2">365 x 450</Menu.Item>
    <Menu.Item key="3">365 x 212 </Menu.Item>
    <Menu.Item key="4">380 x 380 </Menu.Item>
    <Menu.Item key="5">Custom</Menu.Item>
    </Menu>
    );

    handleDownloadClick = (event) => {
        event.preventDefault()
        const {imgSrc}  = this.state
        if (imgSrc) {
            const canvasRef = this.imagePreviewCanvasRef.current

            const {imgSrcExt} =  this.state
            const imageData64 = canvasRef.toDataURL('image/' + imgSrcExt)


            const myFilename = "previewFile." + imgSrcExt

            // file to be uploaded
            const myNewCroppedFile = base64StringtoFile(imageData64, myFilename)
            console.log(myNewCroppedFile)
            // download file
            downloadBase64File(imageData64, myFilename)
            //this.handleClearToDefault()
        }
    }



render(){
    const {imgSrc, croppedImageURL} = this.state
      return (
        <div>




  <PageHeader
      style={{
        border: '1px solid rgb(235, 237, 240)',
      }}
      // onBack={() => window.history.back()}
      title="Image Cropper"
      subTitle="Small utility to crop images and store them locally"


    >

<Descriptions size="small" column={2}>
    <Descriptions.Item label="Created">Richa Sharma</Descriptions.Item>
    <Descriptions.Item label="Tech"> <a href="https://reactjs.org/" target = "_blank">ReactJS</a> and <a href="https://ant.design/docs/react/introduce" target = "_blank">Ant design </a></Descriptions.Item>
    <Descriptions.Item label="Remarks">
      Upload an image and choose the size you wish to crop to. You can choose pre-determined blocks or can do custom crop as well. It accepts images of size 1024x1024 only. 
    </Descriptions.Item>
  </Descriptions>

<Row style={{ padding: '20px 50px' }}>
    <Col span={4}>
        <Dropzone  onDrop={this.onDropFn} accept={FileTypes} multiple={false} maxSize={imgMaxSize}>
            {({getRootProps, getInputProps}) => (
                <section>
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    <Button>
                        <Icon type="upload" /> Click to Upload
                    </Button>
                  </div>
                </section>
            )}
        </Dropzone>
</Col>
<Col span={8}>
        <Dropdown.Button overlay={this.menu}>
        <a className="ant-dropdown-link" href="#">
        Select the crop size
        </a>
        </Dropdown.Button>

</Col>
<Col span={2}>
<Button  disabled={!imgSrc} onClick={this.handleDownloadClick}>Download</Button>
</Col>
<Col span={6}>
<Button   disabled={!imgSrc} onClick={this.handleClearToDefault}>Clear</Button>
</Col>
</Row>
</PageHeader>
        {imgSrc !== null ? 
            <div>
            <Row style={{ padding: '20px 20px' }}> 
            <Col span={12}>
                <ReactCrop src = {imgSrc} 
                crop = {this.state.crop} 
                onImageLoaded = {this.handleImageLoaded}
                onComplete = {this.handleOnCropComplete}
                onChange = {this.handleOnCropChange}/>


            </Col>
            <Col span={4}>
            </Col>
            <Col span={8}>
            <p> Preview Crop </p>
            <canvas ref={this.imagePreviewCanvasRef}></canvas>

              </Col>
              </Row>
            </div>
            : ""}

            {/* croppedImageURL &&  <img alt="Cropped Image" style={{maxWidth: '100%'}} src={croppedImageURL} /> */}

        </div>

      );
    }
}

export default App;
