        }
    };

render(){
      return (
       <Dropzone onDrop={this.onDropFn} accept={FileTypes} multiple={false} maxSize={imgMaxSize}>
            {({getRootProps, getInputProps}) => (
                <section>
                  <div {...getRootProps()}>
                    <input {...getInputProps()} />
                    <p>Drag 'n' drop some files here, or click to select files</p>
                  </div>
                </section>
            )}
        </Dropzone>
      );
    }
}

export default App;
